/*
  y division algorithm, by Kaldewaij
  returns y//x
*/
#include <stdio.h>
#include <limits.h>


int prefunc(int y, int x){
    int q, r, w;

    q = 0;
    r = y;
    w = x;



    if(x == 0){
        printf("%d", 0);
        return 0;
    }

    while (1) {
        if (!(r >= w)) break;
        w = 2 * w;
    }

    while (1) {
        if (!(w != x)) break;

        q = 2 * q;
        w = w / 2;
        if (r >= w) {
            q = q + 1;
            r = r - w;
        }
    }


    int result = r+w*q;

    return result;
}

