int prefunc(int x, int y) {
    int result;
    if (x >= y) {
        if (x >= 5) {
            result = (x + 1)%7;  
        } else {
            result = (x + 5)%7;  
        }
    } else {
        if (x >= 2) {
            result = (x + 5)%7;  
        } else {
            result = x;      
        }
    }
    if (x == y){
        return 7;
    }
    
    return result; 
}

