int* prefunc(int l, int m, int n) {
    static int result[3]; 

    result[0] = l;
    result[1] = m;
    result[2] = n;

    for (int i = 0; i < 2; i++) {
        for (int j = 0; j < 2 - i; j++) {
            if (result[j] > result[j + 1]) {
                int temp = result[j];
                result[j] = result[j + 1];
                result[j + 1] = temp;
            }
        }
    }

    return result;
}

