/* shift_add algorithm for computing the 
   product of two natural numbers
*/

#include <stdio.h>

int prefunc(int x, int y){
    int a, b, z;

    b = y;
    z = 0;
    a = x;

    if (y < 1) {
        printf("%d", z);
        return 0;
    }



    while (b>0) {
        if (b % 2 == 1) {
            z = z + a;
            b = b - 1;
        }
        a = 2 * a;
        b = b / 2;
    }

    return z;
}

